// country code starter here a==================
$(document).ready(function () {
  var validation;
  // get country code list
  let URL =
    "https://www.clubmarriott.in/content/dam/tlc/en/json/countryCodeNew.json";
  $.ajax({
    url: URL,
    type: "GET",
    contentType: "application/json",
    success: function (res) {
      console.log(res);
      res.forEach((element) => {
        const $newStudent = $("#mCountryCodeNew")
          .append(`<li id=${element.countrycode} data-min="${element.Min}" data-max="${element.Max}" val="${element.countrycode}" onclick="showselectedcntNew(${element.countrycode})">
				${element.CountryName}</li>`);
      });
      validation = res;
    },
    error: function (error) {},
  });
});
function showselectedcntNew(valCnt) {
  $("#span_showcntNew").html("+" + valCnt);
  $("#txtMobile").attr("minlength", $("#" + valCnt).attr("data-min"));
  $("#txtMobile").attr("maxlength", $("#" + valCnt).attr("data-max"));
  $("#txtMobile").val("");
}
